content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const alamat = await db.alamat.findUnique({
    where: {
      id: Number(params.id),
    },
  });

  if (!alamat) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Alamat tidak ditemukan",
    });
  }

  const deleteAlamat = await db.alamat.delete({
    where: {
      id: Number(params.id),
    },
  });

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil hapus alamat",
    data: deleteAlamat,
  })
}
