content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "silahkan login dahlu",
    });
  }

  const desc = req.body.desc;
  const id = Number(params.id);

  const transaksi = await db.transaksi.findUnique({
    where: {
      id: id,
    },
  });
  if (!transaksi) {
    reply.status(404).send({
      status: "ERROR",
      message: "Transaki tidak ditemukan",
    });
  }

  const track = await db.track_order.create({
    data: {
      desc: desc,
      id_transaksi: id,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil memasukkan tracking",
    data: track,
  })
}
