content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const id = Number(params.id);

  const transaksi = await db.transaksi.findUnique({
    where: {
      id: id,
    },
  });
  if (!transaksi) {
    reply.status(404).send({
      status: "ERROR",
      message: "Transaki tidak ditemukan",
    });
  }

  const track = await db.track_order.findMany({
    where: {
      id_transaksi: id,
    },
    select: {
      id_transaksi: true,
      created_at: true,
      track_template: {
        select: {
          title: true,
          desc: true,
        },
      },
    },
    orderBy: {
      created_at: "desc",
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil track order",
    data: track,
  })
}
