content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let time = new Date();
  time.setHours(14, 58, 0, 0);

  ext.socket.emit("admin", {
    user_id: 1,
    event: "notif",
    time: time.getTime(),
    data: { nama: "tes", oke: "notif" },
  });

  reply.send({
    time: time,
    hasil: time.getTime() - new Date().getTime(),
  })
}
