content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const customer = await db.customer.findUnique({
    where: {
      email: req.body.email,
    },
  });

  if (customer == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Akun tidak ditemukan",
    });
  }

  let token = await db.token_reset_pass.findUnique({
    where: {
      id_user: customer.id,
    },
  });

  if (token == null) {
    token = await db.token_reset_pass.create({
      data: {
        id_user: customer.id,
        token: ext.crypto.randomBytes(32).toString("hex"),
      },
    });
  }

  const link = `http://localhost:3200/api/customer/password-reset/${customer.id}/${token.token}`;

  const send = await ext.sendEmail(
    customer.email,
    "Password reset",
    `<h3>Silahkan klik link dibawah ini untuk mereset password</h3><br/><a href='${link}'>Link Reset Password</a>`
  );

  if (!send) {
    return reply.status(400).send({
      status: "ERROR",
      message: "Email tidak terkirim",
    });
  }

  return reply.send({
    status: "SUCCESS",
    message: "Email terkirim",
  })
}
