content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role != "super") {
    return reply.send({
      message: "Hanya super admin yang bisa mengakses ini",
    });
  }

  const page = req.query.page ? req.query.page : 1;
  const perPage = req.query.perPage ? req.query.perPage : 10;
  const search = req.query.search ? req.query.search : "";

  const admin = await db.admin.findMany({
    skip: Number((page - 1) * perPage),
    take: Number(perPage),
    where: {
      OR: [
        {
          username: {
            contains: search,
            mode: "insensitive",
          },
        },
        {
          nama: {
            contains: search,
            mode: "insensitive",
          },
        },
        {
          no_hp: {
            contains: search,
            mode: "insensitive",
          },
        },
        {
          email: {
            contains: search,
            mode: "insensitive",
          },
        },
      ],
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data admin",
    data: admin,
  })
}
