content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }

  const detail = await db.transaksi.findFirst({
    where: {
      id: Number(params.id),
    },
    select: {
      id: true,
      tanggal_peminjaman: true,
      tanggal_pengembalian: true,
      total_harga: true,
      status: true,
      bank: true,
      alamat: true,
      detail_transaksi: {
        select: {
          id: true,
          quantity: true,
          total_harga: true,
          barang: {
            select: {
              id: true,
              nama_barang: true,
              harga_barang: true,
              gambar_barang: true,
            },
          },
        },
      },
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get order detail",
    data: detail,
  })
}
