content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest" || user.user.role == "customer") {
    return reply.send({
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const {
    nama_barang,
    merk_barang,
    detail_barang,
    stok_barang,
    id_kategori,
    harga_barang,
  } = req.body;

  try {
    const barang = await db.barang.update({
      where: {
        id: Number(params.id),
      },
      data: {
        nama_barang,
        merk_barang,
        detail_barang,
        stok_barang: Number(stok_barang),
        kategori_barang: Number(id_kategori),
        harga_barang: Number(harga_barang),
      },
    });

    return reply.send({
      status: "SUCCESS",
      message: "Berhasil update data barang",
      data: barang,
    });
  } catch (err) {
    return reply.status(400).send({
      status: "ERROR",
      message: err.message,
    });
  }
}
