content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  if (user.user.role != "super") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya super admin yang bisa mengakses ini",
    });
  }

  const admin = await db.admin.delete({
    where: {
      id: Number(params.id),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil delete data admin",
    data: admin,
  })
}
