content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role == "customer") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const { id_track_template } = req.body;

  const cek = await db.track_order.findFirst({
    where: {
      AND: {
        id_transaksi: Number(params.id),
        id_track_template: Number(id_track_template),
      },
    },
    include: {
      track_template: true,
    },
  });

  if (cek) {
    return reply.status(409).send({
      status: "ERROR",
      message: "Pesanan ini sudah pernah disini",
      data: cek,
    });
  }

  const create = await db.track_order.create({
    data: {
      id_transaksi: Number(params.id),
      id_track_template: Number(id_track_template),
    },
  });

  if (create) {
    const track = await db.track_order.findFirst({
      where: {
        id: create.id,
      },
      include: {
        track_template: true,
      },
    });

    return reply.status(201).send({
      status: "SUCCESS",
      message: "Berhasil melakukan update tracking",
      data: track,
    });
  }
}
