async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { kategori, e } = req?.body;
  console.log(kategori);
  let res = "Failure";
  if (e === "Create") {
    const createkategori = await db.kategori.create({
      data: { nama: kategori },
    }); //insert database
    kategori = createkategori;
    res = "Insert Success";
  }
  if (e === "Update") {
    const updatekategori = await db.kategori.update({
      where: {
        id: kategori.id,
      },
      data: { ...kategori },
    });
    // console.log(updateUser);
    kategori = updatekategori;
    res = "Update Success";
  }
  if (e === "ReadAll") {
    const readkategori = await db.kategori.findMany({
      orderBy: {
        id: "asc",
      },
    });
    kategori = readkategori;
    // console.log(kategori);
    res = "Read kategori Success";
  }
  if (e === "ReadById") {
    const readkategori = await db.kategori.findFirst({
      where: {
        id: kategori.id,
      },
    });
    kategori = readkategori;
    // console.log(user.kategori.name);
    res = kategori ? "Read kategori Success" : "kategori not found";
  }
  if (e === "Delete") {
    try {
      kategori = await db.kategori.delete({
        where: {
          id: kategori.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Searching") {
    const key = "%" + kategori.nama + "%";
    const readkategori = await db.kategori.findMany({
      where: {
        nama: {
          contains: key,
        },
      },
    });
    kategori = readkategori;
    // console.log(authors);
    res = "Read nama Success";
  }
  reply.send({
    res: res,
    kategori,
    e,
  })
}
