content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let kategoris = await db.kategori.findMany({
    select: {
      id: true,
      nama: true,
    },
  });

  let kategori = kategoris.map((item) => item.nama);

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get kategori",
    data: kategori,
    ilul: kategoris,
  })
}
