content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const kategori = await db.kategori.findMany({
    select: {
      nama: true,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get kategori",
    data: kategori,
  })
}
