content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest" || user.user.role == "customer") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }

  const password = await ext.argon2.hash(req.body.password);

  await db.admin.update({
    where: {
      id: Number(params.id),
    },
    data: {
      password: password,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil merubah password",
  })
}
