content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  if (user.user.role == "customer") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const admin = await db.admin.findUnique({
    where: {
      id: Number(params.id),
    },
    select: {
      password: true,
    },
  });
  const oldPassword = await req.body.oldPassword;
  const verify = await ext.argon2.verify(admin.password, oldPassword);
  if (!verify) {
    return reply.status(403).send({
      status: "ERROR",
      message: "invalid old password",
    });
  }

  if (oldPassword == req.body.password) {
    return reply.status(403).send({
      status: "ERROR",
      message: "The new password cannot be the same as the old password",
    });
  }

  const password = await ext.argon2.hash(req.body.password);

  await db.admin.update({
    where: {
      id: Number(params.id),
    },
    data: {
      password: password,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil merubah password",
  })
}
