async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { transaksi, e } = req?.body;
  console.log(transaksi);
  let res = "Failure";
  if (e === "Create") {
    const createtransaksi = await db.transaksi.create({
      data: { ...transaksi },
    }); //insert database
    res = "Insert Success";
  }
  if (e === "Update") {
    const updatetransaksi = await db.transaksi.update({
      where: {
        id: transaksi.id,
      },
      data: { ...transaksi },
    });
    // console.log(updateUser);
    transaksi = updatetransaksi;
    res = "Update Success";
  }
  if (e === "ReadAll") {
    const readtransaksi = await db.transaksi.findMany({
      orderBy: {
        id: "asc",
      },
      include: {
        customer: true,
      },
    });
    transaksi = readtransaksi;
    // console.log(transaksi);
    res = "Read transaksi Success";
  }
  if (e === "ReadById") {
    const readtransaksi = await db.transaksi.findFirst({
      where: {
        id: transaksi.id,
      },
    });
    transaksi = readtransaksi;
    // console.log(user.transaksi.name);
    res = transaksi ? "Read transaksi Success" : "transaksi not found";
  }
  if (e === "Delete") {
    try {
      transaksi = await db.transaksi.delete({
        where: {
          id: transaksi.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Searching") {
    const key = "%" + transaksi.status_transaksi + "%";
    const readtransaksi = await db.transaksi.findMany({
      where: {
        status_transaksi: {
          contains: key,
        },
      },
    });
    transaksi = readtransaksi;
    // console.log(authors);
    res = "Read status_transaksi Success";
  }
  reply.send({
    res: res,
    transaksi,
    e,
  })
}
