content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const alamat = await db.alamat.findFirst({
    where: {
      AND: [
        {
          id: Number(params.id_alamat),
        },
        {
          id_customer: Number(params.id_customer),
        },
      ],
    },
  });

  if (!alamat) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Alamat tidak tersedia",
    });
  }

  const customer = await db.customer.update({
    where: {
      id: Number(params.id_customer),
    },
    data: {
      alamat_utama: Number(params.id_alamat),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil menjadikan alamat utama",
    data: customer,
  })
}
