content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  if (user.user.role == "customer") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const { nama_customer, nama_perusahaan, no_hp, status, email } = req.body;

  try {
    const customer = await db.customer.update({
      where: {
        id: Number(params.id),
      },
      data: {
        nama_customer,
        nama_perusahaan,
        email,
        no_hp: Number(no_hp),
        status: Number(status),
      },
    });

    return reply.send({
      status: "SUCCESS",
      message: "Berhasil update data Customer",
      data: customer,
    });
  } catch (err) {
    return reply.status(400).send({
      status: "ERROR",
      message: err.message,
    });
  }
}
