content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  if (user.user.role != "super") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya super admin yang bisa mengakses ini",
    });
  }

  const { username, email, nama, no_hp, password } = req.body;
  const passwordhash = await ext.argon2.hash(password ? password : "123asd");

  try {
    const admin = await db.admin.create({
      data: {
        username: username,
        password: passwordhash,
        email: email,
        nama: nama,
        no_hp: no_hp,
        role: false,
      },
    });

    reply.status(201).send({
      status: "SUCCESS",
      message: "Berhasil membuat admin baru",
      data: admin,
    });
  } catch (err) {
    reply.status(400).send({
      status: "ERROR",
      message: err.message,
    });
  }
}
