content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const barang = await db.barang.findUnique({
    where: {
      id: Number(req.body.id),
    },
  });

  if (barang == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Barang dengan ID yang dimasukkan tidak ditemukan",
    });
  }

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil get data barang",
    data: barang,
  })
}
