content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { barang, e } = req?.body;
  console.log(barang);
  let res = "Failure";
  if (e === "Create") {
    const createbarang = await db.barang.create({
      data: { ...barang },
    }); //insert database
    res = "Insert Success";
  }
  if (e === "Update") {
    const updatebarang = await db.barang.update({
      where: {
        id: barang.id,
      },
      data: { ...barang },
    });
    // console.log(updateUser);
    barang = updatebarang;
    res = "Update Success";
  }
  if (e === "ReadAllData") {
    const readbarang = await db.barang.findMany({
      orderBy: {
        id: "asc",
      },
    });
    barang = readbarang;
    // console.log(barang);
    res = "Read barang Success";
  }
  if (e === "ReadById") {
    const readbarang = await db.barang.findFirst({
      where: {
        id: barang.id,
      },
    });
    barang = readbarang;
    // console.log(user.barang.name);
    res = barang ? "Read barang Success" : "barang not found";
  }
  if (e === "Delete") {
    try {
      barang = await db.barang.delete({
        where: {
          id: barang.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Searching") {
    const key = "%" + barang.nama_barang + "%";
    const readbarang = await db.barang.findMany({
      where: {
        nama_barang: {
          contains: key,
        },
      },
    });
    barang = readbarang;
    // console.log(authors);
    res = "Read nama_barang Success";
  }
  reply.send({
    res: res,
    barang,
    e,
  })
}
