content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role == "customer") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  try {
    const data = await req.file({
      limits: {
        fileSize: 2000000, // 2MB
      },
    });

    const file = data.filename.split(".");
    const filename = file.slice(0, -1).join(".");
    // const mimeType = data.mimetype;
    const extension = file[file.length - 1];

    const valid_extension = ["png", "img", "svg", "jpg", "jpeg"];
    if (!valid_extension.includes(extension)) {
      return reply.status(400).send({
        status: "ERROR",
        message: "File yang dimasukkan harus gambar",
      });
    }

    const path = !!data.fields.path ? data.fields.path.value : "/";

    const name =
      (!!data.fields.name ? data.fields.name.value : filename) +
      Date.now() +
      "." +
      extension;

    const dir = ext.path.join(
      __dirname,
      "..",
      "..",
      "..",
      "uploads",
      "customer",
      "poto",
      path
    );

    const fullpath = dir + name;

    if (!ext.fs.existsSync(dir)) {
      ext.fs.mkdirSync(dir, { recursive: true });
    }

    await ext.pump(data.file, ext.fs.createWriteStream(fullpath));
    const filePath = await ext.path
      .join("/upload/customer/poto", path, name)
      .replace(/\\/gi, "/");

    const customer = await db.customer.update({
      where: {
        id: Number(params.id),
      },
      data: {
        foto: filePath,
      },
    });

    reply.send({
      status: "ok",
      path: filePath,
    });
  } catch (e) {
    reply.status(400).send({
      status: "failed",
      reason: e.toString(),
    });
  }
}
