content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const barang = await db.barang.delete({
    where: {
      id: Number(params.id),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil delete data barang",
    data: barang,
  })
}
