content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  // if (req.session.user) {
  //   return reply.status(409).send({
  //     status: "ERROR",
  //     message: "Anda sudah login, silahkan logout jika ingin ganti akun",
  //     data: req.session.user,
  //   });
  // }

  const { email, password } = req.body;
  const customer = await db.customer.findUnique({
    where: {
      email: email,
    },
  });

  if (!customer) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Akun dengan email yang dimasukkan tidak terdaftar",
    });
  }

  const pass_verify = await ext.argon2.verify(customer.password, password);

  if (!pass_verify) {
    return reply.status(403).send({
      status: "ERROR",
      message: "Password yang anda masukkan salah",
    });
  }

  req.session.user = customer;

  reply.status(200).send({
    status: "SUCCESS",
    message: "Berhasil login",
    data: customer,
    session: req.session.user,
  })
}
