content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { email, password, no_hp, alamat, nama_perusahaan } = req.body;
  const pass_encript = await ext.argon2.hash(password);

  const customer = await db.customer.create({
    data: {
      email: email,
      password: pass_encript,
      no_hp: no_hp,
      alamat: alamat,
      nama_perusahaan: nama_perusahaan,
    },
  });

  reply.status(201).send({
    status: "SUCCESS",
    message: "Berhasil membuat akun baru",
    data: customer,
  })
}
