content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const notif = await db.notif.findMany({
    where: {
      id_customer: Number(params.id),
    },
    orderBy: {
      created_at: "desc",
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get list all notif",
    data: notif,
  })
}
