content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role != "guest") {
    return reply.status(409).send({
      status: "ERROR",
      message: "Anda sudah login, silahkan logout jika ingin ganti akun",
      data: user.user,
    });
  }

  const { username, password, email } = req.body;
  const admin = await db.admin.findFirst({
    where: {
      OR: [{ username: username }, { email: email }],
    },
  });

  if (!admin) {
    return reply.status(403).send({
      status: "ERROR",
      message: "invalid username/email or password",
    });
  }

  const pass_verify = await ext.argon2.verify(admin.password, password);

  if (!pass_verify) {
    return reply.status(403).send({
      status: "ERROR",
      message: "invalid username/email or password",
    });
  }

  const role = admin.role === 0 ? "super" : "admin";
  user.user = { role, admin };

  reply.status(200).send({
    status: "SUCCESS",
    message: "Berhasil login",
    data: user.user,
  })
}
