content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const customer = await db.customer.delete({
    where: {
      id: Number(req.body.id),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil delete data customer",
    data: customer,
  })
}
