content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role == "customer") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const customer = await db.customer.delete({
    where: {
      id: Number(params.id),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil delete data customer",
    data: customer,
  })
}
