content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const cekAlamatCustomer = await db.customer.findUnique({
    where: {
      id: Number(params.id),
    },
    include: {
      alamat: true,
    },
  });

  const alamat = cekAlamatCustomer.alamat.length;

  reply.send({
    cekAlamatCustomer,
    alamat,
  })
}
