content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }

  const { email, no_hp, nama_perusahaan, nama_customer } = await req.body;

  const customer = await db.customer.update({
    where: {
      id: Number(params.id),
    },
    data: {
      email: email,
      no_hp: no_hp,
      nama_perusahaan: nama_perusahaan,
      nama_customer: nama_customer,
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil update data customer",
    data: customer,
  })
}
