content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const take = req.query.take ? Number(req.query.take) : 10;

  const newBarang = await db.barang.findMany({
    take: take,
    orderBy: {
      created_at: "desc",
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get new barang",
    data: newBarang,
  })
}
