content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const alamat = await db.alamat.findUnique({
    where: {
      id: Number(params.id_alamat),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data satu alamat",
    data: alamat,
  })
}
