async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { admin, e } = req?.body;
  console.log(admin);
  let res = "Failure";
  if (e === "Create") {
    const createadmin = await db.admin.create({
      data: { ...admin },
    }); //insert database
    res = "Insert Success";
  }
  if (e === "Update") {
    const updateadmin = await db.admin.update({
      where: {
        id: admin.id,
      },
      data: { ...admin },
    });
    // console.log(updateadmin);
    admin = updateadmin;
    res = "Update Success";
  }
  if (e === "ReadAll") {
    const readadmin = await db.admin.findMany({
      orderBy: {
        id: "asc",
      },
    });
    admin = readadmin;
    // console.log(admin);
    res = "Read admin Success";
  }
  if (e === "ReadById") {
    const readadmin = await db.admin.findFirst({
      where: {
        id: admin.id,
      },
    });
    admin = readadmin;
    // console.log(user.admin.name);
    res = admin ? "Read admin Success" : "admin not found";
  }
  if (e === "Delete") {
    try {
      admin = await db.admin.delete({
        where: {
          id: admin.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Searching") {
    const key = "%" + admin.username + "%";
    const readadmin = await db.admin.findMany({
      where: {
        username: {
          contains: key,
        },
      },
    });
    admin = readadmin;
    // console.log(authors);
    res = "Read username Success";
  }
  reply.send({
    res: res,
    admin,
    e,
  })
}
