content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const customer = await db.customer.findUnique({
    where: {
      id: Number(req.body.id),
    },
  });

  if (customer == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Customer dengan ID yang dimasukkan tidak ditemukan",
    });
  }

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil get data customer",
    data: customer,
  })
}
