content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role == "customer") {
    return reply.send({
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const customer = await db.customer.findUnique({
    where: {
      id: Number(params.id),
    },
  });

  if (customer == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Customer dengan ID yang dimasukkan tidak ditemukan",
    });
  }

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil get data customer",
    data: customer,
  })
}
