content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }

  const cekCart = await db.detail_transaksi.findFirst({
    where: {
      AND: {
        id: Number(params.id),
        transaksi: {
          id_customer: Number(params.id_customer),
          tanggal_pemesanan: null,
        },
      },
    },
  });

  if (cekCart) {
    const cart = await db.detail_transaksi.delete({
      where: {
        id: cekCart.id,
      },
    });

    return reply.send({
      status: "SUCCESS",
      message: "Berhasil menghapus barang dari dalam keranjang",
      data: cart,
    });
  }

  if (cekCart == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Data barang tidak ditemukan didalam keranjang",
    });
  }
}
