content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const cekCart = await db.detail_transaksi.findFirst({
    where: {
      AND: [
        {
          id_barang: Number(req.body.id_barang),
        },
        {
          transaksi: {
            id_customer: Number(params.id),
          },
        },
      ],
    },
  });

  if (cekCart) {
    const cart = await db.detail_transaksi.delete({
      where: {
        id: cekCart.id,
      },
    });

    return reply.send({
      status: "SUCCESS",
      message: "Berhasil menghapus barang",
      data: cart,
    });
  }

  if (cekCart == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Data barang tidak ditemukan",
    });
  }
}
