content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    kategori_alamat,
    nama,
    no_hp,
    provinsi,
    kota,
    kecamatan,
    alamat_pengiriman,
    kode_pos,
    informasi_tambahan,
  } = req.body;

  const alamat = await db.alamat.create({
    data: {
      id_customer: Number(params.id),
      kategori_alamat,
      nama,
      no_hp,
      provinsi,
      kecamatan,
      kota,
      alamat_pengiriman,
      kode_pos,
      informasi_tambahan,
    },
  });

  reply.status(203).send({
    status: "SUCCESS",
    message: "Berhasil menambah alamt baru",
    data: alamat,
  })
}
