content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.send({
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role != "super") {
    return reply.send({
      message: "Hanya super admin yang bisa mengakses ini",
    });
  }

  const admin = await db.admin.findUnique({
    where: {
      id: Number(params.id),
    },
  });

  if (admin == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Admin dengan ID yang dimasukkan tidak ditemukan",
    });
  }

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil get data admin",
    data: admin,
  })
}
