content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const {
    jenis_barang,
    nama_barang,
    merk_barang,
    detail_barang,
    stok_barang,
  } = req.body;

  try {
    const barang = await db.barang.create({
      data: {
        jenis_barang,
        nama_barang,
        merk_barang,
        detail_barang,
        stok_barang: Number(stok_barang),
      },
    });

    return reply.status(201).send({
      status: "SUCCESS",
      message: "Berhasil menambah barang baru",
      data: barang,
    });
  } catch (err) {
    return reply.status(400).send({
      status: "ERROR",
      message: err.message,
    });
  }
}
