content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }
  if (user.user.role == "customer") {
    return reply.status(403).send({
      status: "ERROR",
      message: "Hanya admin yang bisa mengakses ini",
    });
  }

  const {
    id_kategori,
    nama_barang,
    merk_barang,
    detail_barang,
    stok_barang,
    harga_barang,
  } = req.body;

  try {
    const barang = await db.barang.create({
      data: {
        kategori_barang: Number(id_kategori),
        nama_barang,
        merk_barang,
        detail_barang,
        stok_barang: Number(stok_barang),
        harga_barang: Number(harga_barang),
      },
    });

    return reply.status(201).send({
      status: "SUCCESS",
      message: "Berhasil menambah barang baru",
      data: barang,
    });
  } catch (err) {
    return reply.status(400).send({
      status: "ERROR",
      message: err.message,
    });
  }
}
