async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  let { customer, e } = req?.body;
  console.log(customer);
  let res = "Failure";
  if (e === "Create") {
    const createcustomer = await db.customer.create({
      data: { ...customer },
    }); //insert database
    res = "Insert Success";
  }
  if (e === "Update") {
    const updatecustomer = await db.customer.update({
      where: {
        id: customer.id,
      },
      data: { ...customer },
    });
    // console.log(updateUser);
    customer = updatecustomer;
    res = "Update Success";
  }
  if (e === "ReadAllData") {
    const readcustomer = await db.customer.findMany({
      where: {
        status: true,
      },
      orderBy: {
        id: "asc",
      },
    });
    customer = readcustomer;
    // console.log(customer);
    res = "Read customer Success";
  }
  if (e === "ReadAllDataFalse") {
    const readcustomer = await db.customer.findMany({
      where: {
        OR: [
          {
            status: false,
          },
          { status: null },
        ],
      },
      orderBy: {
        id: "asc",
      },
    });
    customer = readcustomer;
    // console.log(customer);
    res = "Read customer Success";
  }
  if (e === "ReadById") {
    const readcustomer = await db.customer.findFirst({
      where: {
        id: customer.id,
      },
    });
    customer = readcustomer;
    // console.log(user.customer.name);
    res = customer ? "Read customer Success" : "customer not found";
  }
  if (e === "Delete") {
    try {
      customer = await db.customer.delete({
        where: {
          id: customer.id,
        },
      });
      res = "Delete Success";
    } catch (e) {
      console.log(e);
    }
  }
  if (e === "Accept") {
    const acccustomer = await db.customer.update({
      where: {
        id: customer.id,
      },
      data: {
        status: true,
      },
    });
    // console.log(updateUser);
    customer = acccustomer;
    res = "Update Success";
  }
  if (e === "Searching") {
    const key = "%" + customer.nama_customer + "%";
    const readcustomer = await db.customer.findMany({
      where: {
        nama_customer: {
          contains: key,
        },
      },
    });
    customer = readcustomer;
    // console.log(authors);
    res = "Read nama_customer Success";
  }
  if (e === "SearchingStatusFalse") {
    const key = "%" + customer.nama_customer + "%";
    const readcustomer = await db.customer.findMany({
      where: {
        nama_customer: {
          contains: key,
        },
        status: false,
      },
    });
    customer = readcustomer;
    // console.log(authors);
    res = "Read nama_customer Success";
  }
  reply.send({
    res: res,
    customer,
    e,
  })
}
