content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  if (user.user.role == "guest") {
    return reply.status(401).send({
      status: "ERROR",
      message: "Silahkan login terlebih dahulu",
    });
  }

  const { id_barang, quantity } = await req.body;

  const cekCart = await db.transaksi.findFirst({
    where: {
      AND: [
        {
          id_customer: Number(params.id),
        },
        {
          tanggal_pemesanan: null,
        },
      ],
    },
  });

  const barang = await db.barang.findFirst({
    where: {
      id: Number(id_barang),
    },
  });

  const total_harga = barang.harga_barang * quantity;

  if (!cekCart) {
    const cart = await db.transaksi.create({
      data: {
        id_customer: Number(params.id),
        detail_transaksi: {
          create: {
            id_barang: Number(id_barang),
            quantity: Number(quantity),
            total_harga: total_harga,
          },
        },
      },
    });

    return reply.send({
      status: "SUCCESS",
      message: "Berhasil memasukkan barang kedalam keranjang",
      data: cart,
    });
  }

  const cekIfExist = await db.detail_transaksi.findFirst({
    where: {
      AND: {
        id_barang: Number(id_barang),
        id_transaksi: cekCart.id,
      },
    },
  });

  if (cekIfExist) {
    return reply.status(400).send({
      status: "ERROR",
      message: "Barang ini sudah ada dalam keranjang",
      data: cekIfExist,
    });
  }

  const cart = await db.detail_transaksi.create({
    data: {
      id_barang: Number(id_barang),
      quantity: Number(quantity),
      id_transaksi: cekCart.id,
      total_harga: total_harga,
    },
  });

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil memasukkan barang kedalam keranjang",
    data: cart,
  })
}
