content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_barang, quantity } = await req.body;

  const cekCart = await db.transaksi.findFirst({
    where: {
      AND: [
        {
          id_customer: Number(params.id),
        },
        {
          tanggal_pemesanan: null,
        },
      ],
    },
  });

  if (!cekCart) {
    const cart = await db.transaksi.create({
      data: {
        id_customer: Number(params.id),
        detail_transaksi: {
          create: {
            id_barang: Number(id_barang),
            quantity: Number(quantity),
          },
        },
      },
    });

    reply.send({
      status: "SUCCESS",
      message: "Berhasil memasukkan barang kedalam keranjang",
      data: cart,
    });
  } else {
    const cart = await db.detail_transaksi.create({
      data: {
        id_barang: Number(id_barang),
        quantity: Number(quantity),
        id_transaksi: cekCart.id,
      },
    });

    reply.send({
      status: "SUCCESS",
      message: "Berhasil memasukkan barang kedalam keranjang",
      data: cart,
    });
  }
}
