content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const { id_customer, title, desc } = req.body;

  const notif = await db.notif.create({
    data: {
      id_customer,
      title,
      desc,
      created_at: new Date(ext.nowLocal()),
    },
  });

  if (notif) {
    reply.status(201).send({
      status: "SUCCESS",
      data: notif,
    });
  }
}
