content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const bank = await db.bank.findFirst({
    where: {
      id: Number(params.id),
    },
  });

  reply.send({
    status: "SUCCESS",
    message: "Berhasil get data bank",
    data: bank,
  })
}
