content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const quantity = Number(req.body.quantity);

  const cart = await db.detail_transaksi.findFirst({
    where: {
      AND: {
        id: Number(params.id),
        transaksi: {
          id_customer: Number(params.id_customer),
          tanggal_pemesanan: null,
        },
      },
    },
    include: {
      barang: true,
    },
  });

  if (cart == null) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Barang tidak ditemukan didalam keranjang",
    });
  }

  const cartUpdate = await db.detail_transaksi.update({
    where: {
      id: Number(params.id),
    },
    data: {
      quantity,
      total_harga: cart.barang.harga_barang * quantity,
    },
  });

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil update data barang dalam keranjang",
    data: cartUpdate,
  })
}
