content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const cek = await db.customer.findUnique({
    where: {
      id: Number(params.id),
    },
  });

  if (!cek) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Customer dengan ID yang dimasukkan tidak ditemukan",
    });
  }

  if (cek.status) {
    return reply.status(409).send({
      status: "ERROR",
      message: "Customer dengan ID yang dimasukkan sudah di approve",
    });
  }

  const customer = await db.customer.update({
    where: {
      id: Number(params.id),
    },
    data: {
      status: true,
    },
  });

  if (customer) {
    const send = await ext.sendEmail(
      customer.email,
      "Account has been approved",
      `<h3>Akun anda sudah disetujui oleh admin, silahkan menikmati fasilitas yang ada</h3>`
    );

    if (!send) {
      console.log("Email tidak terkirim");
    }
  }

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil approve customer",
    data: customer,
  })
}
