content.server_on_load:
async ({
  template,
  params,
  render,
  db,
  req,
  reply,
  user,
  log,
  ext,
  isDev,
}: Server) => {
  const cek = await db.customer.findUnique({
    where: {
      id: Number(params.id),
    },
  });

  if (!cek) {
    return reply.status(404).send({
      status: "ERROR",
      message: "Customer dengan ID yang dimasukkan tidak ditemukan",
    });
  }

  if (cek.status) {
    return reply.status(409).send({
      status: "ERROR",
      message: "Customer dengan ID yang dimasukkan sudah di approve",
    });
  }

  const customer = await db.customer.update({
    where: {
      id: Number(req.body.id),
    },
    data: {
      status: true,
    },
  });

  return reply.send({
    status: "SUCCESS",
    message: "Berhasil approve customer",
    data: customer,
  })
}
